<style>
  nav {
    position: fixed;
    top: 0;
    right: -50%;
    width: 50%;
    height: 100%;
    background-color: #333;
    transition: all 0.5s ease-in-out;
    z-index: 700;
  }

  nav.open {
    right: 0;
  }

  .sticky-nav {
    position: fixed;
    width: 100vw;
    top: 0;
  }

  .background {
    background-color: #111111;
  }

  #overlay {
    display: none;
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.7);
    z-index: 100;
  }

  .socials {
    color: #BBBBBB;
  }

  .footer {
    border-top: 1px solid white;
    position: absolute;
    bottom: 0;
  }
</style>
